package com.dongpakka.basic_1_edittext

import android.content.Intent
import android.graphics.Color
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.text.Editable
import android.text.TextWatcher
import android.util.Log
import android.widget.EditText
import com.bumptech.glide.Glide
import kotlinx.android.synthetic.main.activity_main.*



class MainActivity : AppCompatActivity() {

    companion object {
        const val REQUEST = 0
        const val ID = "ID"
        const val PASSWD = "PASSWD"
        const val RESULT = "RESULT"
    }

    val TAG = "edittext::MainActivity"

    // R.mipmap.sample1 --> 데이터타입: Int
    // 이미지 ID의 리스트를 변수 (images)에 정의
    val images = listOf<Int>(R.mipmap.sample1, R.mipmap.sample2, R.mipmap.sample3)
    var current_image = 0

    // image의 Url list
    val imageUrls = listOf<String>(
        "https://helpx.adobe.com/content/dam/help/en/photoshop/using/convert-color-image-black-white/jcr_content/main-pars/before_and_after/image-before/Landscape-Color.jpg",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
    )

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

//        imageView.setImageResource(images[current_image])


        loadImage(imageUrls[current_image])


        btn_prev.setOnClickListener {
            --current_image
            if (current_image < 0) {
                current_image = images.size - 1
            }
            Log.i(TAG, "이전버튼 : $current_image, ${imageUrls[current_image]}")
            loadImage(imageUrls[current_image])

        }

        btn_next.setOnClickListener {
            current_image = (current_image + 1) % images.size
            Log.i(TAG, "다음버튼 : $current_image, ${imageUrls[current_image]}")
            loadImage(imageUrls[current_image])
        }


        edtName.setOnFocusChangeListener { v, hasFocus ->
            val edt = v as EditText
            val color = if (hasFocus) Color.TRANSPARENT else Color.LTGRAY
            edt.setBackgroundColor(color)
        }
//        edtPassWD.addTextChangedListener(MyTextWatcher()) 이렇게 하면 앙대
        edtPassWD.addTextChangedListener(object : TextWatcher {
            override fun beforeTextChanged(p0: CharSequence?, p1: Int, p2: Int, p3: Int) {
            }

            override fun onTextChanged(s: CharSequence?, p1: Int, p2: Int, p3: Int) {
                txtMessage1.text = s
            }

            override fun afterTextChanged(p0: Editable?) {
            }
        })

        btnLogin.setOnClickListener {
            val i = Intent(this, ResultActivity::class.java)
            i.putExtra(ID, edtName.text.toString())
            i.putExtra(PASSWD, edtPassWD.text.toString())
            startActivityForResult(i, REQUEST)
        }


    }

    override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {
        if(requestCode != REQUEST) return           // 방어코드
        Log.i("MainActivity", "onActivityResult $data")

        data?.getStringExtra(RESULT).let{
            txtMessage1.text = it
        }
        super.onActivityResult(requestCode, resultCode, data)
    }

    fun loadImage(url: String) = Glide.with(this)
        .load(url)
        .into(imageView)
}